package cn.meowrain.thread_;

public class Thread01 {
    public static void main(String[] args) {
        //创建一个Cat对象,可以当做线程使用
        Cat cat = new Cat();
        cat.start();//启动线程
    }
}
//当一个类继承了Thread类,该类就是可以当线程使用
// run Thread类实现了Runnable接口的run方法

class Cat extends Thread {
    int times = 1;
    @Override
    public void run() {//重写run方法,写自己的业务逻辑
        //该线程每隔1秒在控制台输出"cat,i'm a cat"
        while (true) {
            System.out.println("cat,i'm a cat" + times++);
            try {
                Thread.sleep(1000);
            } catch (InterruptedException e) {
                e.printStackTrace();
            }
            if(times == 80) break;
        }
    }
}
