    String name;
    int age;
    String item;

    public rel_09(String name, int age) {
        this.name = name;
        this.age = age;
    }

    // 构造器重载
    public rel_09(String name, int age, String item) {
        this.name = name;
        this.age = age;
        this.item = item;
    }

    public void getInfo() {
        System.out.println(this.name + "  " + this.age);
    }

    public void getItem() {
        System.out.println(this.item);
    }
}
